LL read() {
    LL n = 0;
    char a = getchar();
    bool flag = 0;
    while(a > '9' || a < '0') { if(a == '-') flag = 1; a = getchar(); }
    while(a <= '9' && a >= '0') { n = n * 10 + a - '0',a = getchar(); }
	if(flag) n = -n;
	return n;
}

int main() {
	freopen("std.in","r",stdin);
	freopen("6433.txt","w",stdout);
	int T = read();
	while(T --) {
		int n = read();
		printf("%.0f\n",pow(2,n));
	}
	return 0;
}
